# Méthode 1 : avec des accolades
mon_set = {1, 2, 3, 4, 5}
print(mon_set)  # {1, 2, 3, 4, 5}

# Méthode 2 : avec la fonction set()
mon_set2 = set([1, 2, 3, 4, 5])
print(mon_set2)  # {1, 2, 3, 4, 5}

# Set vide (attention : {} crée un dict, pas un set !)
set_vide = set()
print(type(set_vide))  # <class 'set'>

# Les doublons sont automatiquement supprimés
avec_doublons = {1, 2, 2, 3, 3, 3}
print(avec_doublons)  # {1, 2, 3}

# Les sets sont optimisés pour les vérifications d'appartenance (O(1) vs O(n) pour les listes)
utilisateurs_actifs = {'alice', 'bob', 'charlie'}

if 'alice' in utilisateurs_actifs:
    print("Alice est connectée")

# Plus rapide qu'une liste pour de grandes collections

liste1 = ['a', 'b', 'c', 'd']
liste2 = ['c', 'd', 'e', 'f']

# Éléments communs
communs = set(liste1) & set(liste2)
print(communs)  # {'c', 'd'}

# Éléments uniques à chaque liste
uniques_liste1 = set(liste1) - set(liste2)
uniques_liste2 = set(liste2) - set(liste1)
print(uniques_liste1)  # {'a', 'b'}
print(uniques_liste2)  # {'e', 'f'}

class Article:
    def __init__(self, titre):
        self.titre = titre
        self.tags = set()

    def ajouter_tag(self, tag):
        self.tags.add(tag)

    def a_tag(self, tag):
        return tag in self.tags

    def tags_communs(self, autre_article):
        return self.tags & autre_article.tags

article1 = Article("Python pour débutants")
article1.ajouter_tag('python')
article1.ajouter_tag('débutant')
article1.ajouter_tag('programmation')

article2 = Article("Python avancé")
article2.ajouter_tag('python')
article2.ajouter_tag('avancé')

print(article1.tags_communs(article2))  # {'python'}

# Créer une exception personnalisée
class ErreurAgeInvalide(Exception):
    """Exception levée quand l'âge est invalide"""
    pass

class ErreurSoldeInsuffisant(Exception):
    """Exception levée quand le solde est insuffisant"""
    def __init__(self, solde, montant_requis):
        self.solde = solde
        self.montant_requis = montant_requis
        message = f"Solde insuffisant : {solde}€ disponible, {montant_requis}€ requis"
        super().__init__(message)

# Utilisation
def verifier_age(age):
    if age < 0:
        raise ErreurAgeInvalide("L'âge ne peut pas être négatif")
    if age > 150:
        raise ErreurAgeInvalide("L'âge semble invalide")
    return age

def retirer_argent(solde, montant):
    if montant > solde:
        raise ErreurSoldeInsuffisant(solde, montant)
    return solde - montant

# Test
try:
    verifier_age(-5)
except ErreurAgeInvalide as e:
    print(f"Erreur : {e}")

def calculer_racine_carree(nombre):
    if not isinstance(nombre, (int, float)):
        raise TypeError("Le nombre doit être un entier ou un flottant")
    if nombre < 0:
        raise ValueError("Le nombre doit être positif ou nul")
    return nombre ** 0.5

# Utilisation
try:
    resultat = calculer_racine_carree(-4)
except ValueError as e:
    print(f"Erreur de valeur : {e}")
except TypeError as e:
    print(f"Erreur de type : {e}")

class CompteBancaire:
    def __init__(self, solde_initial=0):
        if solde_initial < 0:
            raise ValueError("Le solde initial ne peut pas être négatif")
        self.solde = solde_initial

    def deposer(self, montant):
        if montant <= 0:
            raise ValueError("Le montant à déposer doit être positif")
        self.solde += montant

    def retirer(self, montant):
        if montant <= 0:
            raise ValueError("Le montant à retirer doit être positif")
        if montant > self.solde:
            raise ValueError(f"Solde insuffisant. Solde actuel : {self.solde}€")
        self.solde -= montant
        return self.solde

# Utilisation
compte = CompteBancaire(100)
try:
    compte.retirer(150)
except ValueError as e:
    print(f"Opération refusée : {e}")

def lire_fichier(nom_fichier):
    try:
        with open(nom_fichier, 'r') as f:
            return f.read()
    except FileNotFoundError:
        raise FileNotFoundError(f"Le fichier '{nom_fichier}' est introuvable")
    except PermissionError:
        raise PermissionError(f"Permission refusée pour lire '{nom_fichier}'")
    except Exception as e:
        raise RuntimeError(f"Erreur lors de la lecture de '{nom_fichier}': {e}") from e

def traiter_fichier(nom_fichier):
    try:
        contenu = lire_fichier(nom_fichier)
        return contenu.upper()
    except FileNotFoundError as e:
        print(f"Fichier non trouvé : {e}")
        return None

# Test
traiter_fichier("fichier_inexistant.txt")

def diviser(a, b):
    if b == 0:
        raise ZeroDivisionError("Division par zéro impossible")
    if not isinstance(a, (int, float)) or not isinstance(b, (int, float)):
        raise TypeError("Les deux arguments doivent être des nombres")
    return a / b

# Alternative avec assert (peut être désactivé avec -O)
def diviser_avec_assert(a, b):
    assert b != 0, "Le diviseur ne peut pas être zéro"
    assert isinstance(a, (int, float)), "Le premier argument doit être un nombre"
    assert isinstance(b, (int, float)), "Le deuxième argument doit être un nombre"
    return a / b

