class Cache:
    def __init__(self):
        self._cache = {}
        self._cles_accessibles = set()

    def ajouter(self, cle, valeur):
        if cle is None:
            raise ValueError("La clé ne peut pas être None")
        if not isinstance(cle, (str, int)):
            raise TypeError("La clé doit être une chaîne ou un entier")

        if cle in self._cles_accessibles:
            raise ValueError(f"La clé '{cle}' existe déjà dans le cache")

        self._cache[cle] = valeur
        self._cles_accessibles.add(cle)

    def obtenir(self, cle):
        if cle is None:
            raise ValueError("La clé ne peut pas être None")

        if cle not in self._cles_accessibles:
            raise KeyError(f"Clé '{cle}' introuvable dans le cache")

        return self._cache[cle]

    def supprimer(self, cle):
        if cle is None:
            raise ValueError("La clé ne peut pas être None")

        if cle not in self._cles_accessibles:
            raise KeyError(f"Clé '{cle}' introuvable")

        del self._cache[cle]
        self._cles_accessibles.discard(cle)

    def cles_communes(self, autre_cache):
        if autre_cache is None:
            raise ValueError("L'autre cache ne peut pas être None")
        if not isinstance(autre_cache, Cache):
            raise TypeError("L'argument doit être une instance de Cache")

        return self._cles_accessibles & autre_cache._cles_accessibles

    def vider(self):
        self._cache.clear()
        self._cles_accessibles.clear()

# Utilisation
cache1 = Cache()
cache2 = Cache()

try:
    cache1.ajouter("user1", {"nom": "Alice"})
    cache1.ajouter("user2", {"nom": "Bob"})

    cache2.ajouter("user2", {"nom": "Bob"})
    cache2.ajouter("user3", {"nom": "Charlie"})

    # Clés communes
    communes = cache1.cles_communes(cache2)
    print(f"Clés communes : {communes}")  # {'user2'}

    # Obtenir une valeur
    user = cache1.obtenir("user1")
    print(f"Utilisateur : {user}")

    # Tentative d'ajout d'une clé existante
    cache1.ajouter("user1", {"nom": "Alice2"})
except ValueError as e:
    print(f"Erreur : {e}")
except KeyError as e:
    print(f"Erreur de clé : {e}")
