// Exemple avec Fetch API
fetch('https://api.example.com/users')
  .then(response => response.json())
  .then(data => console.log(data));

fetch('https://jsonplaceholder.typicode.com/posts', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    title: 'Mon nouveau post',
    body: 'Contenu de mon post',
    userId: 1
  })
})
.then(response => response.json())
.then(data => console.log('R√©ponse:', data));

fetch('https://jsonplaceholder.typicode.com/posts/99999')
  .then(response => {
    console.log('Status:', response.status);
    return response.json();
  })
  .then(data => console.log('R√©ponse:', data))
  .catch(error => console.error('Erreur:', error));

fetch('https://api.example.com/users')
  .then(response => {
    if (!response.ok) {
      throw new Error('Erreur r√©seau');
    }
    return response.json();
  })
  .then(data => {
    console.log('Donn√©es re√ßues:', data);
  })
  .catch(error => {
    console.error('Erreur:', error);
  });

async function fetchUsers() {
  try {
    const response = await fetch('https://api.example.com/users');
    if (!response.ok) {
      throw new Error('Erreur r√©seau');
    }
    const data = await response.json();
    console.log('Donn√©es re√ßues:', data);
  } catch (error) {
    console.error('Erreur:', error);
  }
}

// Le client envoie une requ√™te HTTP
fetch('https://api.example.com/users')
  .then(response => response.json())
  .then(data => {
    // Le serveur r√©pond avec des donn√©es JSON
    console.log(data); // [{ id: 1, name: 'John' }, ...]
  });

// 1. R√©cup√©rer tous les utilisateurs
fetch('https://api.example.com/users')
  .then(response => response.json())
  .then(users => console.log(users));

// 2. Cr√©er un nouvel utilisateur
fetch('https://api.example.com/users', {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    name: 'Alice',
    email: 'alice@example.com'
  })
})
  .then(response => response.json())
  .then(newUser => console.log('Utilisateur cr√©√©:', newUser));

// 3. Mettre √† jour un utilisateur
fetch('https://api.example.com/users/123', {
  method: 'PATCH',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({ name: 'Alice Updated' })
});

// 4. Supprimer un utilisateur
fetch('https://api.example.com/users/123', {
  method: 'DELETE'
});

// Cr√©er un utilisateur avec des donn√©es JSON
fetch('https://api.example.com/users', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'  // Important !
  },
  body: JSON.stringify({  // Convertir l'objet JavaScript en JSON
    name: 'Alice',
    email: 'alice@example.com',
    age: 30
  })
})
  .then(response => response.json())  // Parser la r√©ponse JSON
  .then(data => console.log(data));

fetch('https://api.example.com/users/123')
  .then(response => {
    // V√©rifier le code de statut
    if (!response.ok) {
      if (response.status === 404) {
        throw new Error('Utilisateur introuvable');
      } else if (response.status === 401) {
        throw new Error('Authentification requise');
      } else {
        throw new Error(`Erreur ${response.status}`);
      }
    }
    return response.json();
  })
  .then(data => console.log('Succ√®s:', data))
  .catch(error => console.error('Erreur:', error));
