import json
from pathlib import Path

FICHIER = "contacts.json"

def charger_contacts():
    """Charge les contacts depuis le fichier JSON."""
    try:
        with open(FICHIER, "r", encoding="utf-8") as f:
            return json.load(f).get("contacts", [])
    except FileNotFoundError:
        return []
    except json.JSONDecodeError:
        print("Erreur : fichier JSON invalide")
        return []

def sauvegarder_contacts(contacts):
    """Sauvegarde les contacts dans le fichier JSON."""
    with open(FICHIER, "w", encoding="utf-8") as f:
        json.dump({"contacts": contacts}, f, indent=2, ensure_ascii=False)
    print("Contacts sauvegard√©s !")

def afficher_contacts(contacts):
    """Affiche tous les contacts."""
    if not contacts:
        print("Aucun contact.")
        return
    print("\n=== CONTACTS ===")
    for c in contacts:
        print(f"[{c['id']}] {c['nom']} - {c['telephone']} - {c['email']}")

def ajouter_contact(contacts):
    """Ajoute un nouveau contact."""
    nouveau_id = max([c['id'] for c in contacts], default=0) + 1
    contact = {
        "id": nouveau_id,
        "nom": input("Nom : "),
        "telephone": input("T√©l√©phone : "),
        "email": input("Email : ")
    }
    contacts.append(contact)
    print(f"Contact '{contact['nom']}' ajout√© !")

def rechercher_contact(contacts):
    """Recherche un contact par nom."""
    terme = input("Rechercher : ").lower()
    resultats = [c for c in contacts if terme in c['nom'].lower()]
    afficher_contacts(resultats)

def supprimer_contact(contacts):
    """Supprime un contact par ID."""
    try:
        id_sup = int(input("ID √† supprimer : "))
        for i, c in enumerate(contacts):
            if c['id'] == id_sup:
                del contacts[i]
                print("Contact supprim√© !")
                return
        print("ID non trouv√©.")
    except ValueError:
        print("ID invalide.")

def menu():
    contacts = charger_contacts()
    while True:
        print("\n=== MENU ===")
        print("1. Afficher les contacts")
        print("2. Ajouter un contact")
        print("3. Rechercher")
        print("4. Supprimer")
        print("5. Sauvegarder")
        print("6. Quitter")

        choix = input("Choix : ")
        if choix == "1":
            afficher_contacts(contacts)
        elif choix == "2":
            ajouter_contact(contacts)
        elif choix == "3":
            rechercher_contact(contacts)
        elif choix == "4":
            supprimer_contact(contacts)
        elif choix == "5":
            sauvegarder_contacts(contacts)
        elif choix == "6":
            print("Au revoir !")
            break

if __name__ == "__main__":
    menu()

class CompteBancaire:
    def __init__(self, titulaire, solde=0):
        self.titulaire = titulaire
        self.solde = solde

    def deposer(self, montant):
        if montant > 0:
            self.solde += montant
            print(f"D√©p√¥t de {montant}‚Ç¨. Nouveau solde : {self.solde}‚Ç¨")

    def retirer(self, montant):
        if montant > self.solde:
            print("Solde insuffisant !")
        else:
            self.solde -= montant
            print(f"Retrait de {montant}‚Ç¨. Nouveau solde : {self.solde}‚Ç¨")

