from abc import ABC, abstractmethod

class Document(ABC):
    """Classe abstraite repr√©sentant un document."""

    def __init__(self, titre, auteur, annee):
        self.titre = titre
        self.auteur = auteur
        self.annee = annee
        self._emprunte = False

    @property
    def est_disponible(self):
        return not self._emprunte

    def emprunter(self):
        if self._emprunte:
            print(f"'{self.titre}' est d√©j√† emprunt√©.")
            return False
        self._emprunte = True
        print(f"'{self.titre}' emprunt√© avec succ√®s.")
        return True

    def retourner(self):
        self._emprunte = False
        print(f"'{self.titre}' retourn√©.")

    @abstractmethod
    def description(self):
        pass

    def __str__(self):
        statut = "Disponible" if self.est_disponible else "Emprunt√©"
        return f"{self.titre} ({self.auteur}, {self.annee}) - {statut}"


class Livre(Document):
    def __init__(self, titre, auteur, annee, nb_pages, genre):
        super().__init__(titre, auteur, annee)
        self.nb_pages = nb_pages
        self.genre = genre

    def description(self):
        return f"Livre: {self.titre}, {self.nb_pages} pages, Genre: {self.genre}"


class Magazine(Document):
    def __init__(self, titre, auteur, annee, numero, mois):
        super().__init__(titre, auteur, annee)
        self.numero = numero
        self.mois = mois

    def description(self):
        return f"Magazine: {self.titre} n¬∞{self.numero}, {self.mois} {self.annee}"


class Bibliotheque:
    """G√®re une collection de documents."""

    def __init__(self, nom):
        self.nom = nom
        self._documents = []

    def ajouter(self, document):
        self._documents.append(document)
        print(f"'{document.titre}' ajout√© √† la biblioth√®que.")

    def rechercher(self, terme):
        terme = terme.lower()
        resultats = [
            doc for doc in self._documents
            if terme in doc.titre.lower() or terme in doc.auteur.lower()
        ]
        return resultats

    def afficher_disponibles(self):
        print(f"\n=== Documents disponibles √† {self.nom} ===")
        for doc in self._documents:
            if doc.est_disponible:
                print(f"  - {doc}")

    def __len__(self):
        return len(self._documents)


