# Code √† refactoriser
def calcMoy(n):
    s=0
    for i in n:
        s=s+i
    return s/len(n)

class userManager:
    def __init__(self):
        self.users=[]

    def addUser(self, name, age):
        self.users.append({"name":name, "age":age})

    def getOldestUser(self):
        oldest=None
        for u in self.users:
            if oldest==None or u["age"]>oldest["age"]:
                oldest=u
        return oldest
