def compter_voyelles(texte):
    voyelles = "aeiouAEIOU"
    compteur = 0

    for lettre in texte:
        if lettre in voyelles:
            compteur += 1

    return compteur

# Test
resultat = compter_voyelles("Bonjour")
print(f"Nombre de voyelles: {resultat}")  # Nombre de voyelles: 3

def est_premier(n):
    if n < 2:
        return False

    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False

    return True

# Tests
print(est_premier(7))   # True
print(est_premier(10))   # False
print(est_premier(17))   # True

