# Gestionnaire de t√¢ches
taches = []

while True:
    print("\n=== Gestionnaire de t√¢ches ===")
    print("1. Ajouter une t√¢che")
    print("2. Afficher toutes les t√¢ches")
    print("3. Marquer une t√¢che comme termin√©e")
    print("4. Supprimer une t√¢che")
    print("5. Quitter")

    choix = input("Votre choix: ")

    if choix == "1":
        description = input("Description de la t√¢che: ")
        taches.append({"description": description, "terminee": False})
        print("T√¢che ajout√©e !")

    elif choix == "2":
        if not taches:
            print("Aucune t√¢che.")
        else:
            print("\n=== Liste des t√¢ches ===")
            for index, tache in enumerate(taches, start=1):
                statut = "‚úì" if tache["terminee"] else "‚óã"
                print(f"{index}. {statut} {tache['description']}")

    elif choix == "3":
        if not taches:
            print("Aucune t√¢che.")
        else:
            print("\n=== Liste des t√¢ches ===")
            for index, tache in enumerate(taches, start=1):
                statut = "‚úì" if tache["terminee"] else "‚óã"
                print(f"{index}. {statut} {tache['description']}")

            try:
                num = int(input("\nNum√©ro de la t√¢che √† marquer comme termin√©e: "))
                if 1 <= num <= len(taches):
                    taches[num - 1]["terminee"] = True
                    print("T√¢che marqu√©e comme termin√©e !")
                else:
                    print("Num√©ro invalide.")
            except ValueError:
                print("Veuillez entrer un nombre valide.")

    elif choix == "4":
        if not taches:
            print("Aucune t√¢che.")
        else:
            print("\n=== Liste des t√¢ches ===")
            for index, tache in enumerate(taches, start=1):
                statut = "‚úì" if tache["terminee"] else "‚óã"
                print(f"{index}. {statut} {tache['description']}")

            try:
                num = int(input("\nNum√©ro de la t√¢che √† supprimer: "))
                if 1 <= num <= len(taches):
                    tache_supprimee = taches.pop(num - 1)
                    print(f"T√¢che '{tache_supprimee['description']}' supprim√©e !")
                else:
                    print("Num√©ro invalide.")
            except ValueError:
                print("Veuillez entrer un nombre valide.")

    elif choix == "5":
        print("Au revoir !")
        break

    else:
        print("Choix invalide. Veuillez choisir entre 1 et 5.")
