def compter_mots(mots):
    compteur = {}

    for mot in mots:
        if mot in compteur:
            compteur[mot] += 1
        else:
            compteur[mot] = 1

    return compteur

# Version avec get()
def compter_mots_v2(mots):
    compteur = {}
    for mot in mots:
        compteur[mot] = compteur.get(mot, 0) + 1
    return compteur

# Test
mots = ["pomme", "banane", "pomme", "orange", "banane", "pomme"]
resultat = compter_mots(mots)
print(resultat)  # {'pomme': 3, 'banane': 2, 'orange': 1}

def inverser_dict(d):
    """Inverse un dictionnaire (cl√©s ‚Üî valeurs)."""
    inverse = {}

    for cle, valeur in d.items():
        inverse[valeur] = cle

    return inverse

# Test
d = {"a": 1, "b": 2, "c": 3}
resultat = inverser_dict(d)
print(resultat)  # {1: 'a', 2: 'b', 3: 'c'}

# Version avec dict comprehension
inverse = {valeur: cle for cle, valeur in d.items()}
print(inverse)  # {1: 'a', 2: 'b', 3: 'c'}

def filtrer_dict(d, seuil):
    """Filtre un dictionnaire en gardant les valeurs > seuil."""
    resultat = {}

    for cle, valeur in d.items():
        if valeur > seuil:
            resultat[cle] = valeur

    return resultat

# Version avec dict comprehension
def filtrer_dict_v2(d, seuil):
    return {cle: valeur for cle, valeur in d.items() if valeur > seuil}

# Test
d = {"a": 10, "b": 5, "c": 15, "d": 3}
resultat = filtrer_dict(d, 7)
print(resultat)  # {'a': 10, 'c': 15}

def calculer_moyennes(etudiants):
    """Calcule et ajoute la moyenne de chaque √©tudiant."""
    for nom, infos in etudiants.items():
        notes = infos["notes"]
        if notes:  # Si la liste n'est pas vide
            moyenne = sum(notes) / len(notes)
            infos["moyenne"] = round(moyenne, 2)
        else:
            infos["moyenne"] = 0

    return etudiants

