    print("2. Afficher tous les livres")
    print("3. Emprunter un livre")
    print("4. Retourner un livre")
    print("5. Afficher les livres disponibles")
    print("6. Quitter")

    choix = input("Votre choix: ")

    if choix == "1":
        titre = input("Titre du livre: ")
        auteur = input("Auteur: ")

        livre_id = compteur_id
        livres[livre_id] = {
            "titre": titre,
            "auteur": auteur,
            "disponible": True,
            "emprunteur": None
        }
        compteur_id += 1
        print(f"Livre ajout√© avec l'ID {livre_id} !")

    elif choix == "2":
        if not livres:
            print("Aucun livre dans la biblioth√®que.")
        else:
            print("\n=== Liste des livres ===")
            for livre_id, infos in livres.items():
                statut = "Disponible" if infos["disponible"] else f"Emprunt√© par {infos['emprunteur']}"
                print(f"ID {livre_id}: {infos['titre']} par {infos['auteur']} - {statut}")

    elif choix == "3":
        if not livres:
            print("Aucun livre dans la biblioth√®que.")
        else:
            print("\n=== Livres disponibles ===")
            disponibles = {id: livre for id, livre in livres.items() if livre["disponible"]}

            if not disponibles:
                print("Aucun livre disponible.")
            else:
                for livre_id, infos in disponibles.items():
                    print(f"ID {livre_id}: {infos['titre']} par {infos['auteur']}")

                try:
                    livre_id = int(input("\nID du livre √† emprunter: "))
                    if livre_id in livres:
                        livre = livres[livre_id]
                        if livre["disponible"]:
                            emprunteur = input("Nom de l'emprunteur: ")
                            livre["disponible"] = False
                            livre["emprunteur"] = emprunteur
                            print(f"Livre '{livre['titre']}' emprunt√© par {emprunteur} !")
                        else:
                            print("Ce livre est d√©j√† emprunt√©.")
                    else:
                        print("ID de livre invalide.")
                except ValueError:
                    print("Veuillez entrer un nombre valide.")

    elif choix == "4":
        if not livres:
            print("Aucun livre dans la biblioth√®que.")
        else:
            try:
                livre_id = int(input("ID du livre √† retourner: "))
                if livre_id in livres:
                    livre = livres[livre_id]
                    if not livre["disponible"]:
                        livre["disponible"] = True
                        emprunteur = livre["emprunteur"]
                        livre["emprunteur"] = None
                        print(f"Livre '{livre['titre']}' retourn√© par {emprunteur} !")
                    else:
                        print("Ce livre est d√©j√† disponible.")
                else:
                    print("ID de livre invalide.")
            except ValueError:
                print("Veuillez entrer un nombre valide.")

    elif choix == "5":
        disponibles = {id: livre for id, livre in livres.items() if livre["disponible"]}

        if not disponibles:
            print("Aucun livre disponible.")
        else:
            print("\n=== Livres disponibles ===")
            for livre_id, infos in disponibles.items():
                print(f"ID {livre_id}: {infos['titre']} par {infos['auteur']}")

    elif choix == "6":
