# ============================================
# EXEMPLE 3 : Propri√©t√©s avec @property (m√©thode Pythonique)
# ============================================

class Personne:
    def __init__(self, nom, age):
        self.__nom = nom
        self.__age = age

    # @property transforme une m√©thode en attribut (lecture)
    # Permet d'utiliser personne.nom au lieu de personne.get_nom()
    @property
    def nom(self):
        return self.__nom

    @property
    def age(self):
        return self.__age

    # @age.setter permet de d√©finir un setter pour la propri√©t√©
    @age.setter
    def age(self, valeur):
        if valeur >= 0:
            self.__age = valeur
        else:
            print("L'√¢ge doit √™tre positif")

# Cr√©er un objet
personne = Personne("Alice", 25)

# Utiliser comme un attribut (mais c'est en fait une m√©thode)
print(personne.nom)  # Alice (appelle automatiquement la m√©thode nom())
print(personne.age)  # 25

# Modifier via le setter
personne.age = 30  # Appelle automatiquement age.setter
print(personne.age)  # 30

personne.age = -5  # L'√¢ge doit √™tre positif
print(personne.age)  # 30 (inchang√©)

# ============================================
# EXEMPLE 3 : Autres m√©thodes sp√©ciales courantes
# ============================================

class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def __str__(self):
        return f"Point({self.x}, {self.y})"

    # __add__ : d√©finit l'addition (+)
    def __add__(self, autre):
        return Point(self.x + autre.x, self.y + autre.y)

    # __sub__ : d√©finit la soustraction (-)
    def __sub__(self, autre):
        return Point(self.x - autre.x, self.y - autre.y)

    # __mul__ : d√©finit la multiplication (*)
    def __mul__(self, nombre):
        return Point(self.x * nombre, self.y * nombre)

# Cr√©er des points
p1 = Point(1, 2)
p2 = Point(3, 4)

# Utiliser les op√©rateurs (appellent les m√©thodes sp√©ciales)
print(p1 + p2)  # Point(4, 6) (appelle __add__)
print(p2 - p1)  # Point(2, 2) (appelle __sub__)
print(p1 * 3)   # Point(3, 6) (appelle __mul__)

# ============================================
# CORRECTION : CompteBancaire avec h√©ritage
# ============================================

class CompteBancaire:
    def __init__(self):
        # Attribut priv√©
        self.__solde = 0

    def deposer(self, montant):
        if montant > 0:
            self.__solde += montant
            print(f"D√©p√¥t de {montant}‚Ç¨")
        else:
            print("Le montant doit √™tre positif")

    def retirer(self, montant):
        if montant > 0 and montant <= self.__solde:
            self.__solde -= montant
            print(f"Retrait de {montant}‚Ç¨")
        else:
            print("Montant invalide ou solde insuffisant")

    def get_solde(self):
        return self.__solde

# Classe enfant
class CompteEpargne(CompteBancaire):
    def calculer_interets(self, taux):
        # taux est un pourcentage (ex: 0.05 pour 5%)
        interets = self.get_solde() * taux
        self.deposer(interets)  # Utiliser la m√©thode h√©rit√©e
        print(f"Int√©r√™ts de {interets}‚Ç¨ ajout√©s")

# Cr√©er un compte √©pargne
compte = CompteEpargne()

# Utiliser les m√©thodes
compte.deposer(1000)  # D√©p√¥t de 1000‚Ç¨
print(f"Solde : {compte.get_solde()}‚Ç¨")  # Solde : 1000‚Ç¨

compte.calculer_interets(0.05)  # Int√©r√™ts de 50.0‚Ç¨ ajout√©s
print(f"Solde apr√®s int√©r√™ts : {compte.get_solde()}‚Ç¨")  # Solde apr√®s int√©r√™ts : 1050.0‚Ç¨
