"""Classes de donn√©es pour le gestionnaire de budget."""
from abc import ABC, abstractmethod
from datetime import date
from typing import Optional


class Transaction(ABC):
    """Classe abstraite repr√©sentant une transaction."""

    def __init__(self, montant: float, description: str, 
                 categorie: str, date_trans: Optional[str] = None):
        self._montant = montant
        self._description = description
        self._categorie = categorie
        self._date = date_trans or str(date.today())

    @property
    def montant(self) -> float:
        return self._montant

    @property
    def description(self) -> str:
        return self._description

    @property
    def categorie(self) -> str:
        return self._categorie

    @property
    def date(self) -> str:
        return self._date

    @property
    @abstractmethod
    def type_transaction(self) -> str:
        pass

    @property
    @abstractmethod
    def impact_solde(self) -> float:
        """Montant avec signe (+ pour revenu, - pour d√©pense)."""
        pass

    def __str__(self) -> str:
        signe = "+" if self.impact_solde > 0 else ""
        return f"{self._date} | {signe}{self.impact_solde:.2f}‚Ç¨ | {self._description} ({self._categorie})"

    def __repr__(self) -> str:
        return f"{self.__class__.__name__}({self._montant}, '{self._description}', '{self._categorie}')"

    def to_dict(self) -> dict:
        return {
            "type": self.type_transaction,
            "montant": self._montant,
            "description": self._description,
            "categorie": self._categorie,
            "date": self._date
        }

    @classmethod
    def from_dict(cls, data: dict) -> 'Transaction':
        if data["type"] == "depense":
            return Depense(data["montant"], data["description"], 
                          data["categorie"], data["date"])
        else:
            return Revenu(data["montant"], data["description"], 
                         data["categorie"], data["date"])


class Depense(Transaction):
    """Repr√©sente une d√©pense."""

    @property
    def type_transaction(self) -> str:
        return "depense"

    @property
    def impact_solde(self) -> float:
        return -self._montant


class Revenu(Transaction):
    """Repr√©sente un revenu."""

    @property
    def type_transaction(self) -> str:
        return "revenu"

    @property
    def impact_solde(self) -> float:
        return self._montant


class Budget:
    """Repr√©sente un budget pour une cat√©gorie."""

    def __init__(self, categorie: str, montant_max: float):
        self._categorie = categorie
        self._montant_max = montant_max

    @property
    def categorie(self) -> str:
        return self._categorie

    @property
    def montant_max(self) -> float:
        return self._montant_max

    def verifier(self, total_depenses: float) -> tuple[bool, float]:
        """V√©rifie si le budget est respect√©.
        Retourne (est_depasse, pourcentage_utilise).
        """
        pourcentage = (total_depenses / self._montant_max) * 100
        est_depasse = total_depenses > self._montant_max
        return est_depasse, pourcentage

    def __str__(self) -> str:
        return f"Budget {self._categorie}: {self._montant_max:.2f}‚Ç¨"

    def to_dict(self) -> dict:
        return {self._categorie: self._montant_max}

    @classmethod
    def from_dict(cls, categorie: str, montant: float) -> 'Budget':
        return cls(categorie, montant)

"""Classe Compte : g√®re les transactions et budgets."""
import json
from pathlib import Path
from datetime import date
from typing import List, Optional
from models import Transaction, Depense, Revenu, Budget


class Compte:
    """G√®re un compte avec ses transactions et budgets."""

    def __init__(self, solde_initial: float = 0.0):
        self._solde_initial = solde_initial
        self._transactions: List[Transaction] = []
        self._budgets: dict[str, Budget] = {}

    @property
    def solde_initial(self) -> float:
        return self._solde_initial

    @property
    def solde_actuel(self) -> float:
        return self._solde_initial + sum(t.impact_solde for t in self._transactions)

    @property
    def transactions(self) -> List[Transaction]:
        return self._transactions.copy()

    def ajouter_transaction(self, transaction: Transaction) -> None:
        self._transactions.append(transaction)

        # V√©rifier le budget si c'est une d√©pense
        if isinstance(transaction, Depense):
            self._verifier_budget(transaction.categorie)

    def ajouter_budget(self, budget: Budget) -> None:
        self._budgets[budget.categorie] = budget

    def _verifier_budget(self, categorie: str) -> Optional[str]:
        if categorie not in self._budgets:
            return None

        budget = self._budgets[categorie]
        total = self.total_depenses_categorie(categorie)
        est_depasse, pourcentage = budget.verifier(total)

        if est_depasse:
            return f"üö® ALERTE : Budget '{categorie}' d√©pass√© ! ({total:.2f}‚Ç¨ / {budget.montant_max:.2f}‚Ç¨)"
        elif pourcentage >= 80:
            return f"‚ö†Ô∏è Attention : Budget '{categorie}' √† {pourcentage:.0f}%"
        return None

    def total_depenses_categorie(self, categorie: str, mois: str = None) -> float:
        mois = mois or date.today().strftime("%Y-%m")
        return sum(
            t.montant for t in self._transactions
            if isinstance(t, Depense) 
            and t.categorie == categorie
            and t.date.startswith(mois)
        )

    def filtrer(self, categorie: str = None, type_trans: str = None, 
                mois: str = None) -> List[Transaction]:
        """Filtre les transactions selon les crit√®res."""
        resultat = self._transactions

        if categorie:
            resultat = [t for t in resultat if t.categorie == categorie]
        if type_trans:
            resultat = [t for t in resultat if t.type_transaction == type_trans]
        if mois:
            resultat = [t for t in resultat if t.date.startswith(mois)]

        return resultat

    def statistiques(self) -> dict:
        """Calcule les statistiques du compte."""
        depenses = [t.montant for t in self._transactions if isinstance(t, Depense)]
        revenus = [t.montant for t in self._transactions if isinstance(t, Revenu)]

        return {
            "total_depenses": sum(depenses),
            "total_revenus": sum(revenus),
            "moyenne_depenses": sum(depenses) / len(depenses) if depenses else 0,
            "max_depense": max(depenses) if depenses else 0,
            "nb_transactions": len(self._transactions),
            "epargne": self.solde_actuel - self._solde_initial
        }

    def generer_rapport(self, mois: str = None) -> str:
        """G√©n√®re un rapport textuel."""
        mois = mois or date.today().strftime("%Y-%m")
        trans = self.filtrer(mois=mois)
        stats = self.statistiques()

        rapport = f"""
{'='*50}
üìä RAPPORT - {mois}
{'='*50}

Solde actuel: {self.solde_actuel:.2f}‚Ç¨
Total d√©penses: {stats['total_depenses']:.2f}‚Ç¨
Total revenus: {stats['total_revenus']:.2f}‚Ç¨
√âpargne: {stats['epargne']:+.2f}‚Ç¨

{'‚îÄ'*50}
TRANSACTIONS DU MOIS
{'‚îÄ'*50}
"""
        for t in trans:
            rapport += f"{t}\n"

        return rapport

    # Persistance
    def sauvegarder(self, fichier: str = "budget.json") -> None:
        data = {
            "solde_initial": self._solde_initial,
            "budgets": {k: v.montant_max for k, v in self._budgets.items()},
            "transactions": [t.to_dict() for t in self._transactions]
        }
        with open(fichier, "w", encoding="utf-8") as f:
            json.dump(data, f, indent=2, ensure_ascii=False)

    @classmethod
    def charger(cls, fichier: str = "budget.json") -> 'Compte':
        if not Path(fichier).exists():
            return cls()

        with open(fichier, "r", encoding="utf-8") as f:
            data = json.load(f)

        compte = cls(data.get("solde_initial", 0))

        for cat, montant in data.get("budgets", {}).items():
            compte.ajouter_budget(Budget(cat, montant))

        for t_data in data.get("transactions", []):
            compte._transactions.append(Transaction.from_dict(t_data))

        return compte
