# √Ä partir d'un tuple
mon_tuple = (1, 2, 3, 2)
mon_set = set(mon_tuple)
print(mon_set)  # {1, 2, 3}

# √Ä partir d'une cha√Æne
mon_set2 = set("python")
print(mon_set2)  # {'p', 'y', 't', 'h', 'o', 'n'}

# √Ä partir d'une range
mon_set3 = set(range(5))
print(mon_set3)  # {0, 1, 2, 3, 4}

fruits = {"pomme", "banane"}

# Ajouter plusieurs √©l√©ments depuis un it√©rable
fruits.update(["orange", "kiwi", "pomme"])
print(fruits)  # {'pomme', 'banane', 'orange', 'kiwi'}

# Ajouter depuis un autre ensemble
autres_fruits = {"mangue", "ananas"}
fruits.update(autres_fruits)
print(fruits)  # {'pomme', 'banane', 'orange', 'kiwi', 'mangue', 'ananas'}

# Ajouter depuis une cha√Æne (chaque caract√®re devient un √©l√©ment)
fruits.update("cerise")
print(fruits)  # Contient maintenant 'c', 'e', 'r', 'i', 's' en plus

fruits = {"pomme", "banane", "orange"}

# remove() : l√®ve une erreur si l'√©l√©ment n'existe pas
fruits.remove("banane")
print(fruits)  # {'pomme', 'orange'}
# fruits.remove("kiwi")  # KeyError: 'kiwi'

# discard() : ne fait rien si l'√©l√©ment n'existe pas (pas d'erreur)
fruits.discard("orange")
print(fruits)  # {'pomme'}
fruits.discard("kiwi")  # Pas d'erreur, ne fait rien
print(fruits)  # {'pomme'}

fruits = {"pomme", "banane", "orange"}

# V√©rifier si un √©l√©ment est dans l'ensemble
print("pomme" in fruits)     # True
print("kiwi" in fruits)       # False
print("kiwi" not in fruits)   # True

# Utilisation dans une condition
if "banane" in fruits:
    print("La banane est disponible")
else:
    print("La banane n'est pas disponible")

fruits = {"pomme", "banane", "orange"}

# Parcourir avec une boucle for
for fruit in fruits:
    print(fruit)
# L'ordre d'affichage peut varier car les sets sont non ordonn√©s

# Parcourir avec une liste en compr√©hension
fruits_majuscules = {fruit.upper() for fruit in fruits}
print(fruits_majuscules)  # {'POMME', 'BANANE', 'ORANGE'}

# Obtenir la longueur d'un ensemble
print(len(fruits))  # 3

set1 = {1, 2, 3}
set2 = {3, 4, 5}

# M√©thode 1 : avec l'op√©rateur |
union1 = set1 | set2
print(union1)  # {1, 2, 3, 4, 5}

# M√©thode 2 : avec la m√©thode union()
union2 = set1.union(set2)
print(union2)  # {1, 2, 3, 4, 5}

# Union avec plusieurs ensembles
set3 = {5, 6, 7}
union3 = set1 | set2 | set3
print(union3)  # {1, 2, 3, 4, 5, 6, 7}

set1 = {1, 2, 3, 4}
set2 = {3, 4, 5, 6}

# M√©thode 1 : avec l'op√©rateur &
intersection1 = set1 & set2
print(intersection1)  # {3, 4}

# M√©thode 2 : avec la m√©thode intersection()
intersection2 = set1.intersection(set2)
print(intersection2)  # {3, 4}

# Intersection avec plusieurs ensembles
set3 = {4, 5, 6}
intersection3 = set1 & set2 & set3
print(intersection3)  # {4}

set1 = {1, 2, 3, 4}
set2 = {3, 4, 5, 6}

# M√©thode 1 : avec l'op√©rateur -
difference1 = set1 - set2
print(difference1)  # {1, 2}

# M√©thode 2 : avec la m√©thode difference()
difference2 = set1.difference(set2)
print(difference2)  # {1, 2}

# La diff√©rence n'est pas commutative
difference3 = set2 - set1
print(difference3)  # {5, 6} - diff√©rent de set1 - set2

set1 = {1, 2, 3, 4}
set2 = {3, 4, 5, 6}

# M√©thode 1 : avec l'op√©rateur ^
sym_diff1 = set1 ^ set2
print(sym_diff1)  # {1, 2, 5, 6}

# M√©thode 2 : avec la m√©thode symmetric_difference()
sym_diff2 = set1.symmetric_difference(set2)
print(sym_diff2)  # {1, 2, 5, 6}

# La diff√©rence sym√©trique est commutative
sym_diff3 = set2 ^ set1
print(sym_diff3)  # {1, 2, 5, 6} - m√™me r√©sultat

set1 = {1, 2, 3}
set2 = {4, 5, 6}
set3 = {3, 4, 5}

# V√©rifier si deux ensembles sont disjoints (aucun √©l√©ment commun)
print(set1.isdisjoint(set2))  # True
print(set1.isdisjoint(set3))  # False (3 est commun)
