def min_max(nombres):
    """Retourne le minimum et le maximum d'une liste de nombres."""
    if not nombres:
        return None, None
    return min(nombres), max(nombres)

# Utilisation
valeurs = [3, 1, 4, 1, 5, 9, 2, 6]
mini, maxi = min_max(valeurs)
print(f"Min: {mini}, Max: {maxi}")  # Min: 1, Max: 9
