// Front-end d√©veloppe avec un mock server
// Le contrat OpenAPI g√©n√®re automatiquement des r√©ponses

// Avant que le back-end soit pr√™t :
const mockApi = 'http://localhost:4010'; // Prism mock server

fetch(`${mockApi}/users`, {
  method: 'POST',
  body: JSON.stringify({ name: 'John', email: 'john@example.com' })
})
  .then(res => res.json())
  .then(user => {
    // Utilise les donn√©es mock√©es selon le contrat
    console.log(user); // { id: 1, name: 'John', email: 'john@example.com' }
  });

// Plus tard, on change juste l'URL vers le vrai serveur
const realApi = 'https://api.example.com';
// Le code front-end reste identique !

// Front-end : Utilisation du client g√©n√©r√©
import { ProductsApi, ProductInput } from './api-client';

// Le client est type-safe gr√¢ce √† OpenAPI
const api = new ProductsApi({
  basePath: 'http://localhost:4010' // Mock server
});

// Cr√©er un produit (types v√©rifi√©s √† la compilation)
const newProduct: ProductInput = {
  name: 'Laptop',
  price: 999.99,
  stock: 10
};

try {
  const product = await api.createProduct(newProduct);
  console.log('Produit cr√©√©:', product.data);
} catch (error) {
  // Gestion des erreurs selon le contrat
  if (error.response?.status === 400) {
    console.error('Donn√©es invalides:', error.response.data);
  }
}

// Back-end : Express avec validation OpenAPI
const express = require('express');
const swaggerUi = require('swagger-ui-express');
const { OpenApiValidator } = require('express-openapi-validator');
const openapiSpec = require('./openapi.yaml');

const app = express();

// Validation automatique selon OpenAPI
app.use(
  OpenApiValidator.middleware({
    apiSpec: openapiSpec,
    validateRequests: true,  // Valide les requ√™tes entrantes
    validateResponses: true,  // Valide les r√©ponses sortantes
  })
);

// Route qui respecte le contrat
app.post('/products', async (req, res) => {
  // req.body est d√©j√† valid√© selon le sch√©ma ProductInput
  const product = await createProduct(req.body);

  // La r√©ponse est valid√©e automatiquement
  res.status(201).json(product);
});

// Documentation interactive
app.use('/docs', swaggerUi.serve, swaggerUi.setup(openapiSpec));
