import swaggerUi from 'swagger-ui-express';
import { Express } from 'express';
import fs from 'fs';
import path from 'path';
import * as yaml from 'js-yaml';

export function setupSwagger(app: Express): void {
  const openApiPath = path.join(__dirname, '../openapi/openapi.yaml');
  const openApiFile = fs.readFileSync(openApiPath, 'utf8');
  const openApiSpec = yaml.load(openApiFile) as Record<string, unknown>;

  // Servir le YAML brut
  app.get('/openapi', (req, res) => {
    res.setHeader('Content-Type', 'application/yaml');
    res.send(openApiFile);
  });

  // Servir le JSON
  app.get('/openapi.json', (req, res) => {
    res.json(openApiSpec);
  });

  // Configurer Swagger UI
  app.use('/docs', swaggerUi.serve, swaggerUi.setup(openApiSpec));

  console.log('📚 Swagger UI disponible sur http://localhost:3000/docs');
}

import express from 'express';
import { setupSwagger } from './docs/swagger';

const app = express();
setupSwagger(app);

app.listen(3000, () => {
  console.log('🚀 Serveur démarré sur http://localhost:3000');
  console.log('📚 Swagger UI : http://localhost:3000/docs');
});

export type TaskStatus = 'todo' | 'doing' | 'done';

export interface Task {
  id: string;
  title: string;
  description?: string;
  status: TaskStatus;
  createdAt: string;
  updatedAt: string;
}

export interface TaskCreate {
  title: string;
  description?: string;
  status?: TaskStatus;
}

export interface TaskUpdate {
  title?: string;
  description?: string;
  status?: TaskStatus;
}

export interface TaskQueryParams {
  limit?: number;
  offset?: number;
  status?: TaskStatus;
}

import { v4 as uuidv4 } from 'uuid';
import { Task, TaskCreate, TaskUpdate, TaskStatus, TaskQueryParams } from '../types/task';

class TaskService {
  private tasks: Task[] = [];

  findAll(params: TaskQueryParams): { tasks: Task[]; total: number } {
    let filtered = [...this.tasks];

    // Filtre par status si fourni
    if (params.status) {
      filtered = filtered.filter((task) => task.status === params.status);
    }

    const total = filtered.length;
    const offset = params.offset || 0;
    const limit = params.limit || 10;
    const paginated = filtered.slice(offset, offset + limit);

    return { tasks: paginated, total };
  }

  findById(id: string): Task | undefined {
    return this.tasks.find((task) => task.id === id);
  }

  create(data: TaskCreate): Task {
    const now = new Date().toISOString();
    const task: Task = {
      id: uuidv4(),
      title: data.title,
      description: data.description,
      status: data.status || 'todo',
      createdAt: now,
      updatedAt: now,
    };
    this.tasks.push(task);
    return task;
  }

  update(id: string, data: TaskUpdate): Task | null {
    const task = this.findById(id);
    if (!task) return null;

    const updated: Task = {
      ...task,
      ...data,
      updatedAt: new Date().toISOString(),
    };

    const index = this.tasks.findIndex((t) => t.id === id);
    this.tasks[index] = updated;
    return updated;
  }

  patch(id: string, data: Partial<TaskUpdate>): Task | null {
    const task = this.findById(id);
    if (!task) return null;

    const updated: Task = {
      ...task,
      ...data,
      updatedAt: new Date().toISOString(),
    };

    const index = this.tasks.findIndex((t) => t.id === id);
    this.tasks[index] = updated;
    return updated;
  }

  delete(id: string): boolean {
    const index = this.tasks.findIndex((task) => task.id === id);
    if (index === -1) return false;
    this.tasks.splice(index, 1);
    return true;
  }
}

export const taskService = new TaskService();

import { Request, Response, NextFunction } from 'express';
import { v4 as uuidv4 } from 'uuid';

export interface ApiError {
  code: string;
  message: string;
  details?: unknown;
  traceId?: string;
}

export interface ErrorResponse {
  error: ApiError;
}

export function errorHandler(
  err: Error | ApiError,
  req: Request,
  res: Response,
  next: NextFunction
): void {
  const traceId = uuidv4();

  if ('code' in err && 'message' in err) {
    const apiError: ApiError = {
      code: err.code,
      message: err.message,
      details: err.details,
      traceId,
    };
    const statusCode = getStatusCode(err.code);
    res.status(statusCode).json({ error: apiError });
    return;
  }

  const apiError: ApiError = {
    code: 'INTERNAL_ERROR',
    message: err.message || 'Une erreur interne est survenue',
    traceId,
  };

  console.error(`[${traceId}] Error:`, err);
  res.status(500).json({ error: apiError });
}

export function createError(
  code: string,
  message: string,
  details?: unknown
): ApiError {
  return { code, message, details };
}

function getStatusCode(code: string): number {
  const statusMap: Record<string, number> = {
    VALIDATION_ERROR: 400,
    UNAUTHORIZED: 401,
    NOT_FOUND: 404,
    INTERNAL_ERROR: 500,
  };
  return statusMap[code] || 500;
}

export function notFoundHandler(req: Request, res: Response): void {
  const error: ErrorResponse = {
    error: {
      code: 'NOT_FOUND',
      message: `Route ${req.method} ${req.path} non trouvée`,
      traceId: uuidv4(),
    },
  };
  res.status(404).json(error);
}

import { Request, Response, NextFunction } from 'express';
import { ZodSchema, ZodError } from 'zod';
import { createError } from './errorHandler';

export function validate(schema: {
  body?: ZodSchema;
  query?: ZodSchema;
  params?: ZodSchema;
}) {
  return (req: Request, res: Response, next: NextFunction): void => {
    try {
      if (schema.body) {
        req.body = schema.body.parse(req.body);
      }
      if (schema.query) {
        req.query = schema.query.parse(req.query);
      }
      if (schema.params) {
        req.params = schema.params.parse(req.params);
      }
      next();
    } catch (error) {
      if (error instanceof ZodError) {
        const details = error.errors.map((err) => ({
          path: err.path.join('.'),
          message: err.message,
        }));
        const apiError = createError('VALIDATION_ERROR', 'Erreur de validation', details);
        res.status(400).json({ error: apiError });
        return;
      }
      next(error);
    }
  };
}

import rateLimit from 'express-rate-limit';

export const apiRateLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // Limite de 100 requêtes par IP
  message: {
    error: {
      code: 'RATE_LIMIT_EXCEEDED',
      message: 'Trop de requêtes depuis cette IP, veuillez réessayer plus tard.',
    },
  },
});

import { Router, Request, Response } from 'express';
import { z } from 'zod';
import { taskService } from '../services/taskService';
import { validate } from '../middlewares/validate';
import { createError } from '../middlewares/errorHandler';
import { TaskStatus } from '../types/task';

const router = Router();

// Schémas de validation Zod
const taskStatusSchema = z.enum(['todo', 'doing', 'done']);

const taskCreateSchema = z.object({
  title: z.string().min(3, 'Le titre doit contenir au moins 3 caractères'),
  description: z.string().optional(),
  status: taskStatusSchema.optional(),
});

const taskUpdateSchema = z.object({
  title: z.string().min(3).optional(),
  description: z.string().optional(),
  status: taskStatusSchema.optional(),
});

const taskParamsSchema = z.object({
  id: z.string().uuid('ID doit être un UUID valide'),
});

const taskQuerySchema = z.object({
  limit: z
    .string()
    .optional()
    .transform((val) => (val ? parseInt(val, 10) : undefined))
    .pipe(z.number().int().positive().max(100).optional()),
  offset: z
    .string()
    .optional()
    .transform((val) => (val ? parseInt(val, 10) : undefined))
    .pipe(z.number().int().nonnegative().optional()),
  status: taskStatusSchema.optional(),
});

// GET /health
router.get('/health', (req: Request, res: Response) => {
  res.json({
    status: 'ok',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
  });
});

// GET /tasks
router.get(
  '/tasks',
  validate({ query: taskQuerySchema }),
  (req: Request, res: Response) => {
    const { limit, offset, status } = req.query as {
      limit?: number;
      offset?: number;
      status?: TaskStatus;
    };
    const result = taskService.findAll({ limit, offset, status });
    res.json({
      data: result.tasks,
      pagination: {
        total: result.total,
        limit: limit || 10,
        offset: offset || 0,
      },
    });
  }
);

// GET /tasks/:id
router.get(
  '/tasks/:id',
  validate({ params: taskParamsSchema }),
  (req: Request, res: Response) => {
    const { id } = req.params;
    const task = taskService.findById(id);
    if (!task) {
      const error = createError('NOT_FOUND', `Tâche avec l'ID ${id} non trouvée`);
      res.status(404).json({ error });
      return;
    }
    res.json({ data: task });
  }
);

// POST /tasks
router.post(
  '/tasks',
  validate({ body: taskCreateSchema }),
  (req: Request, res: Response) => {
    const task = taskService.create(req.body);
    res.status(201).json({ data: task });
  }
);

// PUT /tasks/:id
router.put(
  '/tasks/:id',
  validate({
    params: taskParamsSchema,
    body: taskCreateSchema,
  }),
  (req: Request, res: Response) => {
    const { id } = req.params;
    const existingTask = taskService.findById(id);
    if (!existingTask) {
      const error = createError('NOT_FOUND', `Tâche avec l'ID ${id} non trouvée`);
      res.status(404).json({ error });
      return;
    }
    const updated = taskService.update(id, req.body);
    res.json({ data: updated });
  }
);

// PATCH /tasks/:id
router.patch(
  '/tasks/:id',
  validate({
    params: taskParamsSchema,
    body: taskUpdateSchema,
  }),
  (req: Request, res: Response) => {
    const { id } = req.params;
    const updated = taskService.patch(id, req.body);
    if (!updated) {
      const error = createError('NOT_FOUND', `Tâche avec l'ID ${id} non trouvée`);
      res.status(404).json({ error });
      return;
    }
    res.json({ data: updated });
  }
);

// DELETE /tasks/:id
router.delete(
  '/tasks/:id',
  validate({ params: taskParamsSchema }),
  (req: Request, res: Response) => {
    const { id } = req.params;
    const deleted = taskService.delete(id);
    if (!deleted) {
      const error = createError('NOT_FOUND', `Tâche avec l'ID ${id} non trouvée`);
      res.status(404).json({ error });
      return;
    }
    res.status(204).send();
  }
);

export default router;

import express, { Express } from 'express';
import cors from 'cors';
import tasksRouter from './routes/tasks';
import { errorHandler, notFoundHandler } from './middlewares/errorHandler';
import { apiRateLimiter } from './middlewares/rateLimit';
import { setupSwagger } from './docs/swagger';

const app: Express = express();
const PORT = process.env.PORT || 3000;

// Middlewares globaux
app.use(cors());
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Rate limiting (sauf /health et /docs)
app.use((req, res, next) => {
  if (req.path === '/health' || req.path.startsWith('/docs') || req.path.startsWith('/openapi')) {
    return next();
  }
  return apiRateLimiter(req, res, next);
});

// Configuration Swagger UI
setupSwagger(app);

// Routes
app.use('/', tasksRouter);

// Gestion des erreurs (en dernier)
app.use(notFoundHandler);
app.use(errorHandler);

// Démarrage du serveur
app.listen(PORT, () => {
  console.log(`🚀 Serveur démarré sur http://localhost:${PORT}`);
  console.log(`📚 Documentation Swagger UI : http://localhost:${PORT}/docs`);
  console.log(`📄 OpenAPI spec : http://localhost:${PORT}/openapi`);
});

export default app;
